java -Dserver.port=20440  -Dcsp.sentinel.dashboard.server=localhost:20440 -Dproject.name=sentinel-dashboard -Dsentinel.dashboard.autoRemoveMachineMillis=60000 -jar target/sentinel-dashboard.jar
